import{_ as s,c as a,o as n,a as l}from"./app.cf0a218f.js";const d=JSON.parse('{"title":"linux join命令","description":"","frontmatter":{},"headers":[{"level":3,"title":"基本示例","slug":"基本示例","link":"#基本示例","children":[]},{"level":3,"title":"思考问题","slug":"思考问题","link":"#思考问题","children":[]}],"relativePath":"linux/command/jjj/join.md"}'),p={name:"linux/command/jjj/join.md"},o=l(`<h1 id="linux-join命令" tabindex="-1">linux join命令 <a class="header-anchor" href="#linux-join命令" aria-hidden="true">#</a></h1><p>类似数据库中的表join, 左连接, 右链接, 左右都显示的连接</p><ul><li>只能join两个文件</li><li>主键列必须是排序好的</li></ul><p>格式:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#FFCB6B;">Usage:</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">join</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#A6ACCD;">OPTION</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">... </span><span style="color:#C3E88D;">FILE1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">FILE2</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># The default join field is the first, delimited by blanks.</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 默认以第一列作为Join字段, 以空格分割字段</span></span>
<span class="line"><span style="color:#FFCB6B;">-t</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">指定分隔符</span></span>
<span class="line"><span style="color:#FFCB6B;">-a</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">后面跟上文件编号,</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">比如-a1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-a2</span></span>
<span class="line"><span style="color:#FFCB6B;">-i</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">忽略大小写</span></span>
<span class="line"><span style="color:#FFCB6B;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">后面跟上输出的字段,</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">比如1.1第一个文件的第一列,</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">2.3</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">表示第二个文件的第三列</span></span>
<span class="line"><span style="color:#FFCB6B;">-e</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">把输出字段中没有的空白,</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">替换成指定的字符</span></span>
<span class="line"><span style="color:#FFCB6B;">-j</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">指定某一列作为join的主键列,</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">比如</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-jx</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">等于</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-1x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-2x,</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">也就是指定两个文件的x列作为主列</span></span>
<span class="line"><span style="color:#FFCB6B;">-v</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">其中-v1是</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">左边有但右边没有的,</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-v2是右边有但是左边没有的</span></span>
<span class="line"></span></code></pre></div><h3 id="基本示例" tabindex="-1">基本示例 <a class="header-anchor" href="#基本示例" aria-hidden="true">#</a></h3><p><strong>准备数据</strong>:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#FFCB6B;">tee</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;&lt;EOF</span></span>
<span class="line"><span style="color:#C3E88D;">1 January</span></span>
<span class="line"><span style="color:#C3E88D;">2 February</span></span>
<span class="line"><span style="color:#C3E88D;">3 March</span></span>
<span class="line"><span style="color:#C3E88D;">13 Unknown</span></span>
<span class="line"><span style="color:#89DDFF;">EOF</span></span>
<span class="line"></span>
<span class="line"><span style="color:#FFCB6B;">tee</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">2.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;&lt;EOF</span></span>
<span class="line"><span style="color:#C3E88D;">1       一月</span></span>
<span class="line"><span style="color:#C3E88D;">2       二月</span></span>
<span class="line"><span style="color:#C3E88D;">3       三月</span></span>
<span class="line"><span style="color:#C3E88D;">14 故意不对齐的十四月</span></span>
<span class="line"><span style="color:#89DDFF;">EOF</span></span>
<span class="line"></span></code></pre></div><h4 id="交集" tabindex="-1">交集 <a class="header-anchor" href="#交集" aria-hidden="true">#</a></h4><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#FFCB6B;">join</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">2.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 示例输出</span></span>
<span class="line"><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> January 一月</span></span>
<span class="line"><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;"> February 二月</span></span>
<span class="line"><span style="color:#F78C6C;">3</span><span style="color:#A6ACCD;"> March 三月</span></span>
<span class="line"></span></code></pre></div><h4 id="left-join" tabindex="-1">left join <a class="header-anchor" href="#left-join" aria-hidden="true">#</a></h4><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#FFCB6B;">join</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-a1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">2.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 示例输出</span></span>
<span class="line"><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> January 一月</span></span>
<span class="line"><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;"> February 二月</span></span>
<span class="line"><span style="color:#F78C6C;">3</span><span style="color:#A6ACCD;"> March 三月</span></span>
<span class="line"><span style="color:#F78C6C;">13</span><span style="color:#A6ACCD;"> Unknown</span></span>
<span class="line"></span>
<span class="line"></span></code></pre></div><h4 id="right-join" tabindex="-1">right join <a class="header-anchor" href="#right-join" aria-hidden="true">#</a></h4><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#FFCB6B;">join</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-a2</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">2.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 示例输出</span></span>
<span class="line"><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> January 一月</span></span>
<span class="line"><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;"> February 二月</span></span>
<span class="line"><span style="color:#F78C6C;">3</span><span style="color:#A6ACCD;"> March 三月</span></span>
<span class="line"><span style="color:#F78C6C;">14</span><span style="color:#A6ACCD;"> 故意不对齐的十四月</span></span>
<span class="line"></span></code></pre></div><h4 id="左右全都要的join" tabindex="-1">左右全都要的join <a class="header-anchor" href="#左右全都要的join" aria-hidden="true">#</a></h4><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#FFCB6B;">join</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-a1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-a2</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">2.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 示例输出</span></span>
<span class="line"><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> January 一月</span></span>
<span class="line"><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;"> February 二月</span></span>
<span class="line"><span style="color:#F78C6C;">3</span><span style="color:#A6ACCD;"> March 三月</span></span>
<span class="line"><span style="color:#F78C6C;">13</span><span style="color:#A6ACCD;"> Unknown</span></span>
<span class="line"><span style="color:#F78C6C;">14</span><span style="color:#A6ACCD;"> 故意不对齐的十四月</span></span>
<span class="line"></span>
<span class="line"></span></code></pre></div><p>以上输出不知道来自文件1还是文件2, 因此如下</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#FFCB6B;">join</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-a1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-a2</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-e</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">-</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">1.1 1.2 2.1 2.2</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">2.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 示例输出</span></span>
<span class="line"><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> January </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> 一月</span></span>
<span class="line"><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;"> February </span><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;"> 二月</span></span>
<span class="line"><span style="color:#F78C6C;">3</span><span style="color:#A6ACCD;"> March </span><span style="color:#F78C6C;">3</span><span style="color:#A6ACCD;"> 三月</span></span>
<span class="line"><span style="color:#F78C6C;">13</span><span style="color:#A6ACCD;"> Unknown - -</span></span>
<span class="line"><span style="color:#FFCB6B;">-</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">14</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">故意不对齐的十四月</span></span>
<span class="line"></span>
<span class="line"></span></code></pre></div><h4 id="一边有-另一边没有的" tabindex="-1">一边有,另一边没有的 <a class="header-anchor" href="#一边有-另一边没有的" aria-hidden="true">#</a></h4><p><code>-v</code> 参数</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-palenight"><code><span class="line"><span style="color:#FFCB6B;">join</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-v1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">2.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 示例输出</span></span>
<span class="line"><span style="color:#F78C6C;">13</span><span style="color:#A6ACCD;"> Unknown</span></span>
<span class="line"></span>
<span class="line"><span style="color:#FFCB6B;">join</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-v2</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">1.txt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">2.txt</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 示例输出</span></span>
<span class="line"><span style="color:#F78C6C;">14</span><span style="color:#A6ACCD;"> 故意不对齐的十四月</span></span>
<span class="line"></span></code></pre></div><h3 id="思考问题" tabindex="-1">思考问题 <a class="header-anchor" href="#思考问题" aria-hidden="true">#</a></h3><p>sed和awk如何实现join功能?</p>`,23),e=[o];function t(c,r,C,y,i,A){return n(),a("div",null,e)}const h=s(p,[["render",t]]);export{d as __pageData,h as default};
